mapper.update:
UPDATE ITEMS SET 
NAME = #{name}, IMAGE_PATH = #{imagePath}, PRICE = #{price},
DISCOUNT = #{discount}, SCORE = #{score}, CONTENT = #{content},
NUM_STOCK = #{numStock}
WHERE ID = #{id}

mapper.select:
SELECT ${columns} FROM ( SELECT ${columns} FROM ( SELECT ${columns}, ROWNUM AS RN FROM ( SELECT ${columns} FROM ITEMS WHERE PATH LIKE '${path}%'
AND ${key} LIKE '%${value}%'
ORDER BY ID DESC ) ) WHERE RN >= #{paging.startRow}) WHERE ROWNUM <= #{paging.endRow}

SELECT ${columns} FROM ( SELECT ${columns} FROM ( SELECT ${columns}, ROWNUM AS RN FROM ( SELECT ${columns} FROM ITEMS WHERE PATH LIKE '${path}%'
AND NAME LIKE '%${nameSearch}%'
ORDER BY ID DESC ) ) WHERE RN >= #{paging.startRow}) WHERE ROWNUM <= #{paging.endRow}
